sys_variable_value.value:
(function parse(inputs, outputs) {

// Source item string is available in inputs.sourceItem.
// Generate output by setting fields of outputs.targetObject.
// 
// The structure of outputs.targetObject corresponds to the
// structure defined in the Data Stream Action output.

// Parse glacier record properties into the outputs.targetObject
// that is passed to the Data Stream Action Outputs.
 var glacier_rec = JSON.parse(inputs.sourceItem);
 outputs.targetObject.id = glacier_rec.glacier_id;
 outputs.targetObject.name = glacier_rec.name;
 outputs.targetObject.country = glacier_rec.country;
 outputs.targetObject.latitude = glacier_rec.latitude;
 outputs.targetObject.longitude = glacier_rec.longitude;

})(inputs, outputs);

